function desenhaBotaoTimes($mat){
    print'<div class="dropdown">';
    print'<button class="dropbtn">Times futebol</button>';
    print'<div class="dropText">';
    foreach ($mat as $m) {
       print'<span><img src="'.$m["info"].'" width="20" height="20">'.$m["nome"].'</span>'; 
    }
    print'</button>';
    print'</div>';
    pula();
}
